DROP TABLE IF EXISTS `tiku1_wrong_questions`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `tiku1_wrong_questions` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `user_id` int(11) NOT NULL,
  `question_id` int(11) NOT NULL,
  `answer` text COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '用户答案',
  `correct_answer` text COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '正确答案',
  `reviewed` tinyint(1) DEFAULT '0' COMMENT '1：已复习，0：未复习',
  `reviewed_at` datetime DEFAULT NULL,
  `type` tinyint(1) NOT NULL DEFAULT '0' COMMENT '类型：0-错题，1-收藏',
  `created_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`),
  KEY `user_id` (`user_id`),
